query.statement:
SELECT * FROM( SELECT ROW_NUMBER() OVER(ORDER BY A.REG_DT DESC) RM ,COUNT(1) OVER() TOTAL_CNT ,A.* FROM ( SELECT B.REQ_TITLE, CASE WHEN :vo.session_user_locale= 'ko' THEN A.CNTRT_NM ELSE ISNULL(A.CNTRT_NM_EN, A.CNTRT_NM) END AS TITLE, B.REQ_DEPT_NM DEPT_NM, 'TN_CLM_CONTRACT_CNSD' TAB, A.CNTRT_ID + '@' + B.CNSDREQ_ID SEQNO, A.CNTRT_ID, ISNULL(B.MAIN_MATR_CONT,' ') CONT, A.CNTRT_OPPNT_NM COMPANY, B.REG_DT, ISNULL(B.WEEKDUTYYN, 'Y') WEEKDUTYYN, ISNULL(B.CONF_WEEKDUTYYN, 'Y') CONF_WEEKDUTYYN, B.CNSD_DEPT, B.CNSDREQ_ID FROM TN_CLM_CONTRACT_MASTER A, ( SELECT TT.CNTRT_ID, SS.REQ_TITLE, SS.CNSDREQ_ID, SS.REQ_DEPT_NM, TT.REG_DT, E.WEEKDUTYYN, E.CONF_WEEKDUTYYN, E.MAIN_MATR_CONT, E.CNSD_DEPT From TN_CLM_CONT_CNSDREQ SS, TN_CLM_CONTRACT_CNSD TT, ( SELECT CNSDREQ_ID, CNTRT_ID, CNSD_DEPT, WEEKDUTYYN, CONF_WEEKDUTYYN, MAIN_MATR_CONT FROM TN_CLM_CONTRACT_DEPTCNSD ) E WHERE SS.CNSDREQ_ID = TT.CNSDREQ_ID AND TT.CNTRT_ID = E.CNTRT_ID AND TT.CNSDREQ_ID = E.CNSDREQ_ID AND SS.DEL_YN = 'N' AND TT.COMP_CD = :vo.session_comp_cd AND SS.COMP_CD = :vo.session_comp_cd #if (!$vo.display_yn.equals("Y")) AND UPPER(E.WEEKDUTYYN)='Y' #end #if ($vo.result_yn && $vo.result_yn.equals("P")) AND SS.PRGRS_STATUS < 'C04205' #end #if (!$vo.result_yn || $vo.result_yn.equals("R")) AND SS.PRGRS_STATUS IN ('C04207', 'C04205') #end ) B, TN_CLM_CONT_CNSDREQ_RESPMAN D WHERE 1=1 AND A.COMP_CD = :vo.session_comp_cd AND A.CNTRT_ID = B.CNTRT_ID AND B.CNSDREQ_ID = D.CNSDREQ_ID AND D.RESPMAN_ID = :vo.session_user_id #if ($vo.weekFirstDay && !$vo.weekLastDay.equals("")) --AND SUBSTRING(CONVERT(VARCHAR, B.REG_DT, 112),1,6) LIKE :vo.srch_regdt1 + CASE WHEN LENGTH(:vo.srch_regdt2) = 1 THEN '0' + :vo.srch_regdt2 ELSE :vo.srch_regdt2 END + '%' AND CONVERT(VARCHAR, B.REG_DT, 112) BETWEEN REPLACE(:vo.weekFirstDay,'-','') AND REPLACE(:vo.weekLastDay,'-','') #else AND SUBSTRING(CONVERT(VARCHAR, B.REG_DT, 112),1,6) = SUBSTRING(CONVERT(VARCHAR, GETDATE(), 112),1,6) #end ) A
) B
--WHERE B.RM BETWEEN :vo.start_index and :vo.end_index
